mapper.select:
SELECT d.*,p.PROJECTNAME,p.PROJECTNO,u.REALNAME 'PMName' FROM t_demand d,t_project p,t_user u WHERE d.ISDELETE = 0 AND p.ISDELETE = 0 AND p.PMID = u.USERNAME AND d.PROJECTID = p.ID
AND d.CREATENAME = #{userName}
AND d.id = #{id}
ORDER BY d.CREATETIME DESC

SELECT d.*,p.PROJECTNAME,p.PROJECTNO,u.REALNAME 'PMName' FROM t_demand d,t_project p,t_user u WHERE d.ISDELETE = 0 AND p.ISDELETE = 0 AND p.PMID = u.USERNAME AND d.PROJECTID = p.ID
AND d.CREATENAME = #{userName}
ORDER BY d.CREATENAME DESC
