mapper.select:
SELECT d.*,p.PROJECTNAME,p.PROJECTNO FROM t_demand d,t_project p WHERE d.ISDELETE = 0 AND p.ISDELETE = 0 AND d.PROJECTID = p.ID
AND d.CREATENAME = #{userName}
ORDER BY d.CREATETIME DESC

SELECT d.*,p.PROJECTNAME,p.PROJECTNO FROM t_demand d,t_project p WHERE d.ISDELETE = 0 AND p.ISDELETE = 0 AND d.PROJECTID = p.ID
AND d.CREATENAME = #{params.map.userName}
ORDER BY d.CREATETIME DESC LIMIT #{offset},#{pageSize}
